vector<string> split(string str, string separator) {
  vector<string> ret;
      char* mutableStr = new char[str.size() + 1];
    std::strcpy(mutableStr, str.data());
  char *tok = strtok(mutableStr, separator.c_str());
  while (tok != nullptr) {
    ret.push_back(tok);
    tok = strtok(nullptr, separator.c_str());
  }
  return ret;
}

